CREATE TABLE bluetooth (id VARCHAR(100) PRIMARY KEY);
CREATE TABLE motos (id VARCHAR(100) PRIMARY KEY, model VARCHAR(200), id_bluetooth VARCHAR(100), battery_max VARCHAR(100), battery_min VARCHAR(100), FOREIGN KEY(id_bluetooth) REFERENCES bluetooth(id));
CREATE TABLE motos_state(id VARCHAR(100) PRIMARY KEY, moto_id VARCHAR(100), on_state INTEGER, off INTEGER, in_traction INTEGER, no_traction INTEGER, latitude VARCHAR(100), longitude VARCHAR(100), vibracy VARCHAR(100), battery VARCHAR(100), velocity VARCHAR(100), acceleration VARCHAR(100), brake VARCHAR(100), last_change DATETIME, FOREIGN KEY(moto_id) REFERENCES motos(id));
